(62, 'VSDN - EXPLORATION OF SOUTHERN VIETNAM', '', '', '', '<h2 style=\"text-align:justify\"><span style=\"color:#000000\"><span style=\"font-size:28px\">Unveil beauty of Vietnam Southern&nbsp;</span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Discover hectic life and popular landmarks of Ho Chi Minh city, visit incredible underground Cu Chi tunnels, enjoy a boat trip visiting colorful floating market in peaceful Mekong Delta and meet friendly local people. Fall in love with picturesque city of Dalat known as &quot;City of Eternal Spring&quot; with favourable climate, hundreds of beautiful flowers, scenic lakes and waterfalls. Unwind and chill at stunning&nbsp;long sandy beach at the coastal city Nha Trang. Get ready and join in this wonderful trip ever!</span></p>\r\n', '<h3 style=\"text-align:justify\"><span style=\"font-size:18px\">1. Please provide your full name exactly as per passport at the time of booking (including any middle names listed on your passport) for&nbsp;flight tickets.<br />\r\n2. A single supplement is available on this trip, subject to availability. Please contact your sales agent if you would like to book this.&nbsp;<br />\r\n3. This trip includes a domestic flight. Checked luggage allowance is a maximum 20kg, with up to 7kg carry on luggage. Any excess luggage charges will need to be paid directly to the airline at check-in.<br />\r\n4. For group of from 1 - 3 pax, you will be on a shared limousine from Dalat to Nha Trang. If you want a private car, surcharge will be applied.</span><br />\r\n&nbsp;</h3>\r\n', '', 438, 0, 0, 'EXPLORATION-OF-SOUTHERN-VIETNAM-8-days-7-nights-Greentours-Indochina.jpg', '[\"{\\\"image\\\":\\\"VSDN-Dalat-city-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"VSDN-Nha-Trang-beach-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"VSDN-Ho-Chi-Minh-city-Greentours-Indochina.jpg\\\"}\"]', '2019-11-07 10:11:13', '2019-12-26 14:12:59', 0, 0, 0, 0, 0, '', '', '[]', '', '', '', '', '', '7 breakfasts and 3 lunches', 'Private car, plane, limousine and boat', 'Hotel (7 nights)', 'Ho Chi Minh - City tour, Cu Chi tunnels\r\nMekong Delta - Leisurely boat trip, floating market\\\r\nDa Lat - city sightseeing\r\nNha Trang - Relax at the beach\r\n', '', 1, 'VSDN - EXPLORATION OF SOUTHERN VIETNAM', ' Join this amazing trip to explore Southern Vietnam where you can visit vibrant Ho Chi Minh, immerse in dreamy city Dalat and unwind on beautiful sandy beach Nha Trang', 'vsdn-exploration-of-southern-vietnam', 'Southern Vietnam exploration', NULL, '', '', '', NULL, NULL, '148,144,144', 0, 0, '', 'Ho Chi Minh ', 'Nha Trang', 'Vietnam', '', 'Classic', 'VSDN ', '2', '', '', 'VSDN - Greentours Indochina  442x662.png', 'VSDN-Dalat-Greentours-Indochina.jpg', 'Dreamy Dalat city, Vietnam'),
(63, 'VSP - PHU QUOC PARADISE ISLAND TOUR ', '', '', '', '<h2 style=\"text-align:justify\"><span style=\"color:#000000\"><span style=\"font-size:28px\">Phu Quoc - a truly&nbsp;paradise on earth</span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Phu Quoc is one of the top destinations&nbsp;for beach lovers or those seeking for refreshment. Travelers will not only have a chance to indulge in crystal turquoise water, relax&nbsp;at the pristine sandy beaches and palm trees but also take part in a lot of interesting activities such as sunbathing, swimming, scuba-diving, snorkeling and enjoying the sunset.</span></p>\r\n', '<h3 style=\"text-align:justify\"><span style=\"font-size:18px\">1. Please provide your full name exactly as per passport at the time of booking (including any middle names listed on your passport) for flight tickets.<br />\r\n2. A single supplement is available on this trip, subject to availability. Please contact your sales agent if you would like to book this.&nbsp;<br />\r\n3. This trip includes a domestic flight. Checked luggage allowance is a maximum 20kg, with up to 7kg carry on luggage. Any excess luggage charges will need to be paid directly to the airline at check-in.</span><br />\r\n&nbsp;</h3>\r\n', '', 320, 0, 0, 'PHU-QUOC-PARADISE-ISLAND-TOUR-4D3N-Greentours-Indochina.jpg', '[\"{\\\"image\\\":\\\"VSP-Phu-Quoc-island-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"Phu-Quoc-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"Phu Quoc island - Greentours Indochina.jpg\\\"}\"]', '2019-11-07 11:11:03', '2019-12-26 14:12:53', 0, 0, 0, 1, 0, '', '', '[]', '', '', '', '', '', '3 breakfasts and 1 lunch', 'Plane, private car and boat', 'Hotel ( 3 nights)', '- Join Southern tour, snorkel and swim', '', 1, 'VSP - PHU QUOC PARADISE ISLAND TOUR ', 'Phu Quoc is a truly paradise for beach lovers with its crystal turquoise water, pristine sandy beaches, marvelous sunset and interesting water activities.', 'vsp-phu-quoc-paradise-island-tour', 'Phu Quoc Island', NULL, '', '', '', NULL, NULL, '148,144,144', 0, 0, '', 'Ho Chi Minh ', 'Ho Chi Minh ', 'Vietnam', '', 'Classic', 'VSP', '2', '', '', 'VSP - Greentours Indochina  442x662.png', 'VSP-Phu-Quoc-Greentours-Indochina.jpg', 'Sunset in Phu Quoc, Vietnam'),
(64, 'VSN - NHA TRANG BEACH BREAK ', 'As one of the most beautiful coastal cities, Nha Trang will steal your heart for its beautiful long sandy beach, crystal clear blue water and stunning islands.', '', '', '<h2 style=\"text-align:justify\"><span style=\"color:#000000\"><span style=\"font-size:28px\">Unwind&nbsp;at one of the world&#39;s most beautiful bays</span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Known as one of the most beautiful coastal cities&nbsp;in Vietnam, Nha Trang will steal&nbsp;your heart for the first time with its long sandy beach, crystal clear blue water, a lot of breathtaking islands and diverse marine ecosystem. Spend couple days chilling out at the beach, taking part in numerous water activities and taste delicious fresh seafood. A true paradise on earth is waiting for you.</span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:18px\">1. Please provide your full name exactly as per passport at the time of booking (including any middle names listed on your passport).<br />\r\n2. A single supplement is available on this trip, subject to availability. Please contact your sales agent if you would like to book this.&nbsp;<br />\r\n3. An airport arrival transfer is included. Please provide your flight details at the time of booking.</span><br />\r\n&nbsp;</p>\r\n', '', 159, 0, 0, 'NHA-TRANG-BEACH-BREAK-4-days-3-nights-Greentours-Indochina.jpg', '[\"{\\\"image\\\":\\\"VSN-Nha-Trang-Snorkeling-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"VSN-Salt-field-Nha-Trang-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"Nha Trang Snorkeling - Greentours Indochina.jpg\\\"}\"]', '2019-11-07 13:11:49', '2019-12-26 14:12:21', 0, 0, 0, 0, 0, '', '', '[]', '', '', '', '', '', '3 breakfasts and 1 lunch', 'Private car, boat', 'Hotel ( 3 nights)', '- Join islands tour\r\n- Relax at the beach', '', 1, 'VSN - NHA TRANG BEACH BREAK ', 'As one of the most beautiful coastal cities, Nha Trang will steal your heart for its beautiful long sandy beach, crystal clear blue water and stunning islands.', 'vsn-nha-trang-beach-break', 'Nha Trang beach, Nha Trang Islands', NULL, '', '', '', NULL, NULL, '148,144,144', 0, 0, '', 'Nha Trang', 'Nha Trang', 'Vietnam', '', 'Classic', 'VSN ', '2', '', '', 'Nha Trang - Greentours Indochina.jpg', 'VSN-Nha-Trang-Greentours-Indochina.jpg', 'Sunset in Nha Trang, Vietnam'),
(65, 'VNAT - HIKE, BIKE, KAYAK IN NORTHERN', 'This is a perfect trip for adventurers, you will enjoy trekking through rice terraces in Sapa, cycling through scenic mountainous Mai Chau and kayaking in Ha Long Bay. ', '', '', '<h2 style=\"text-align:justify\"><span style=\"color:#000000\"><span style=\"font-size:28px\">Explore Northern Vietnam in an active way</span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">This is absolutely a perfect trip for those seeking for physical activities and loving exploration in Vietnam. Start from Hanoi where you can enjoy the highlighted attractions and learn about the rich culture of the country to mountainous Sapa which is a popular place for hiking and trekking through impressive rice terraces and ethnic villages. Cycle to scenic mountainous Mai Chau town to Ninh Binh city. Enjoy kayaking in the Natural Wonder of the World &ndash; Ha Long Bay.</span></p>\r\n', '<h3 style=\"text-align:justify\"><span style=\"font-size:18px\">1.&nbsp;Please provide your full name exactly as per passport at the time of booking (including any middle names listed on your passport) for local transport tickets.&nbsp;<br />\r\n2.&nbsp;A single supplement is available on this trip, subject to availability. Please contact your sales agent if you would like to book this.&nbsp;<br />\r\n3. Bicycle hire is included in your trip price. Please advise your height at time of booking so we can organise a suitable sized bike.</span><br />\r\n&nbsp;</h3>\r\n', '', 1760, 0, 0, 'HIKE-BIKE-KAYAK-IN-NORTHERN-11-days-10-nights-Greentours-Indochina.jpg', '[\"{\\\"image\\\":\\\"VNAT-Biking-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"VNAT-Sapa-rice-terraces-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"Biking - Greentours Indochina.jpg\\\"}\"]', '2019-11-08 08:11:17', '2019-12-26 14:12:05', 0, 0, 0, 1, 0, '', '', '[]', '', '', '', '', '', '10 breakfasts, 7 lunches and 2 dinners', 'Car, bus, bike, van, boat and cruise', 'Hotel (6 nights)\r\nOvernight bus ( 1 night)\r\nHomestay ( 2 nights)\r\nCruise ( 1 night)', 'Hanoi - city tour\r\nSapa - trek Lao Chai, Ta Van, Giang Ta Chai, Su Pan and Cat Cat villages\r\nMai Chau - cycle and visit Thai village\r\nNinh Binh - Trang An complex\r\nHa Long Bay - cruise, kayak', '', 1, 'VNAT - HIKE, BIKE, KAYAK IN NORTHERN', 'This is a perfect trip for adventurers, you will enjoy trekking through rice terraces in Sapa, cycling through scenic mountainous Mai Chau and kayaking in Ha Long Bay. ', 'vnat-hike-bike-kayak-in-northern', 'Vietnam active tour', NULL, '', '', '', NULL, NULL, '154,144,144', 0, 0, '', 'Hanoi', 'Hanoi', 'Vietnam', '', 'Active Adventure', 'VNAT ', '4', '', '', 'VNAT - Greentours Indochina 442x622.png', 'VNAT-Halong-Greentours-Indochina.jpg', 'Kayaking in Halong bay, Vietnam'),
(66, 'VNC - DISCOVER VIETNAM BY BIKE', 'As home to numerous breathtaking natural landscapes, grandiose highlands and mountains, crystal emerald beaches, Vietnam is one of the best destinations in Southeast Asia for cycling.', '', '', '<h2 style=\"text-align:justify\"><span style=\"color:#000000\"><span style=\"font-size:28px\">Sense endless beauty of Vietnam&nbsp;on a two-wheeled trip</span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">As home to numerous breathtaking natural landscapes, grandiose highlands and mountains, crystal emerald beaches, Vietnam is one of the best destinations in Southeast Asia for cycling.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">During our tour, you will have an opportunity to enjoy timeless charm of Vietnam in a special way from capital city to UNESCO World Heritage, through majestic mountains, along beautiful coastlines to highland central and end at vibrant Ho Chi Minh city. It is also great time for you you to meet warm and friendly people, discover their life and taste mouth-watering food.</span></p>\r\n', '<h3 style=\"text-align:justify\"><span style=\"font-size:18px\">1. Bicycle hire is included in your trip price. Please advise your height at time of booking so as we can organise a suitable sized bike.<br />\r\n2. Bike helmets &amp; closed toed shoes are compulsory on this trip. We are unable to hire bike helmets locally so please ensure you bring your own bike helmet from home.</span></h3>\r\n', '', 2540, 0, 0, 'Discover-Vietnam-by-bike-15-days-14-nights-Greentours-Indochina.jpg', '[\"{\\\"image\\\":\\\"VNC-Vietnam-cycling-01-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"VNC-Vietnam-cycling-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"DISCOVER VIETNAM BY BIKE 15 days 14 nights - Greentours Indochina.jpg\\\"}\"]', '2019-11-08 09:11:43', '2019-12-26 11:12:51', 0, 0, 0, 0, 0, '', '', '[]', '', '', '', '', '', '15 breakfasts, 13 lunches ', 'Bike, van and plane', 'Hotel (15 nights)', 'Hanoi - Ho Chi Minh Mausoleum, One Pillar pagoda, Old Quarter\r\nHanoi - Bat Trang pottery village\r\nHue - Citadel and to Thien Mu Pagoda\r\nHue - Khai Dinh and Tu Duc Royal Tombs\r\nHoi An - My Son Sanctuary\r\nQuang Ngai - My Lai massacre site \r\nNha Trang - City sightseeing and relaxing at the beach\r\nDa Lat - Visit city \'s attractions\r\nHo Chi Minh - City tour by bike\r\n', '', 1, 'VNC - DISCOVER VIETNAM BY BIKE', 'As home to numerous breathtaking natural landscapes, grandiose highlands and mountains, crystal emerald beaches, Vietnam is one of the best destinations in Southeast Asia for cycling.', 'vnc-discover-vietnam-by-bike', 'Vietnam bike tour', NULL, '', '', '', NULL, NULL, '154,144,144', 0, 0, '', 'Hanoi', 'Ho Chi Minh ', 'Vietnam', '', 'Active Adventure', 'VNC ', '4', '', '', 'VNC - Greentours Indochina 442x622.png', 'Vietnam-Cycle-Greentours-Indochina.jpg', 'Cycling in coastal road in central, Vietnam'),
(67, 'VHLN - EXPLORE THE NORTH OF VIETNAM ', 'Enjoy Vietnam\'s beautiful landscapes, delicious cuisine and the renowned friendliness of the Vietnamese people. Unspoiled forests, crystal-clear lakes, long winding rivers, mysterious atmosphere and quiet villages, this is the place where you can indulge yourself in the sounds and colours of nature. ', '', '', '<h2><span style=\"color:#000000\"><span style=\"font-size:28px\">Experience the stunning landscapes in northern Vietnam</span></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Enjoy Vietnam&#39;s beautiful landscapes, delicious cuisine and the renowned friendliness of the Vietnamese people. Unspoiled forests, crystal-clear lakes, long winding rivers, mysterious atmosphere and quiet villages, this is the place where you can indulge yourself in the sounds and colours of nature. Go on an&nbsp;overnight cruise on mystical Halong Bay and you won&#39;t ever want to leave.</span></p>\r\n', '<p style=\"text-align:justify\"><span style=\"font-size:18px\">1. Please provide your full name exactly as per passport at the time of booking (including any middle names listed on your passport). Ticketing fees may apply for amendments to details within 45 days of departure, and in some cases you will be required to cover the cost of issuing a new ticket.<br />\r\n2. A single supplement is available on this trip, subject to availability. Please contact your sales agent if you would like to book this.</span></p>\r\n', '', 551, 0, 0, 'TAM COC-BICH-DONG-DAY-TOUR-Greentours-Indochina-resized.jpg', '[\"{\\\"image\\\":\\\"Halong-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"Hanoi-Greentours-Indochina-1.jpg\\\"}\"]', '2019-11-25 17:11:58', '2020-02-12 10:02:18', 0, 0, 0, 1, 0, '', '', '[]', '', '', '', '', '', '6 breakfasts, 5 lunches and 5 dinners, ', 'Private car, cruise, bike, boat', 'Hotels (6 nights)', 'Hanoi - Temple of Literature\r\nHanoi - One Pillar Pagoda & HCM stilt house\r\nHanoi - Old Quarter Walking Tour\r\nHalong Bay - Overnight boat cruise with seafood lunch and dinner\r\nNinh Binh - King Dinh, Le Temple, Mua Cave, Tam Coc boat trip\r\nHanoi - Bat Trang ceramic village', '', 1, 'VHLN - EXPLORE THE NORTH OF VIETNAM ', 'Unspoiled forests, crystal-clear coastal area, long winding rivers, mysterious atmosphere and quiet villages, this Vietnam tour package is all you need! ', 'vhln-explore-the-north-of-vietnam', 'Vietnam tour', NULL, '', '', '', NULL, NULL, '148,144,144', 0, 0, '', 'Hanoi', 'Hanoi', 'Vietnam', '', 'Classic', 'VHLN', '2', '', '', 'VHLN-Greentours-Indochina-442x622.png', 'VHLN-Vietnam-rice-fields-Greentours-Indochina.jpg', 'Sapa, Lao Cai, Vietnam'),
(68, 'HALONG ONE DAY CRUISE TRIP', 'Admire the wonderful beauty of the Natural Wonder of the World as well as join in interesting activities like kayaking, bamboo boating and swimming.\r\n', '', '', '<p><span style=\"font-size:18px\">Join our trip to admire the wonderful beauty of the Natural Wonder of the World as well as take part in interesting activities like kayaking, bamboo boating and swimming.</span><br />\r\n&nbsp;</p>\r\n', '<p>1. Please provide your full name exactly as per passport at the time of booking (including any middle names listed on your passport).&nbsp;<br />\r\n2. A single supplement is available on this trip, subject to availability. Please contact your sales agent if you would like to book this.</p>\r\n', '', 57, 0, 0, 'Halong-Bay-Cruise-Greentours-Indochina.jpg', '[\"{\\\"image\\\":\\\"Halong-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"Halong-Boating-Greentours-Indochina.jpg\\\"}\"]', '2020-02-04 11:02:16', '2020-02-04 14:02:14', 0, 0, 0, 0, 0, '', '', '[]', '', '', '', '', '', '1 Lunch', 'Shuttle bus, cruise', 'Cruise', '- Sightseeing \r\n- Cooking class\r\n- Rowing boat or kayaking\r\n', '', 1, 'HALONG ONE DAY CRUISE TRIP', '', 'halong-one-day-cruise-trip', '', 1, '', '', '', NULL, NULL, '155,152,152', 0, 0, '', 'Hanoi', 'Hanoi', 'Halong Bay', '', 'Cruising', 'VNHL', '2', '', '', 'HNHL-Greentours Indochina.png', 'Halong-Bay-Greentours-Indochina.jpg', 'Halong Bay Cruise'),
(69, 'HUONG PAGODA ONE DAY TRIP', 'The Huong Pagoda is a vast complex of Buddhist temples and shrines which is also the site of a religious festival which draws large numbers of pilgrims from across Vietnam.', '', '', '<p><span style=\"font-size:18px\">The Huong Pagoda is a vast complex of Buddhist temples and shrines and&nbsp;also the site of a religious festival which draws large numbers of pilgrims from across Vietnam.</span></p>\r\n', '', '', 34, 0, 0, NULL, '[\"{\\\"image\\\":\\\"Perfume-Pagoda.jpg\\\"}\"]', '2020-02-04 14:02:14', '2020-02-05 10:02:17', 0, 0, 0, 0, 0, '', '', '[]', '', '', '', '', '', 'Lunch', 'Private car\r\n', '', '- Sightseeing\r\n- Hiking,\r\n- Boating trip', '', 0, 'HUONG PAGODA ONE DAY TRIP', '', 'huong-pagoda-one-day-trip', '', 1, '', '', '', NULL, NULL, '152,144,144', 0, 0, '', 'Hanoi', 'Hanoi', 'Perfume Pagoda', '', 'Urban Tours', 'VNHP', '2', '', '', '', '', ''),
(70, 'BAVI ECOTOUR', '', '', '', '<p><span style=\"font-size:18px\">The bustling life in big city might make you want to find a place to &quot;escape&quot;, and Ba Vi is just the perfect destination for you to experience interesting farming activities, get close to warm local and enjoy every peaceful moments in the countryside.</span><br />\r\n&nbsp;</p>\r\n', '', '', 76, 0, 0, 'Tea-leaf-harvest-Greentours-Indochina.jpg', '[\"{\\\"image\\\":\\\"Cooking-class-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"Tea-Greentours-Indochina.jpg\\\"}\",\"{\\\"image\\\":\\\"Bavi-Ecotour-Greentours-Indochina.jpg\\\"}\"]', '2020-02-04 14:02:25', '2020-02-05 10:02:35', 0, 0, 0, 0, 0, '', '', '[]', '', '', '', '', '', 'Lunch', 'Private car', '', '- Cooking class\r\n- Tea leaf harvesting\r\n- Visting the village\r\n- Cycling and boating.', '', 0, 'BAVI ECOTOUR', '', 'bavi-ecotour', '', 1, '', '', '', NULL, NULL, '152,144,144', 0, 0, '', 'Hanoi', 'Hanoi', 'Ba Vi', '', 'Urban Tours', 'HBV', '2', '', '', '', 'HBV-Bavi-Ecotour-Greentours-Indochina.jpg', '');

